CurrencyInputExampleIntegration:
import CurrencyInput from '@growth-ui/react/addons/CurrencyInput';
import Input from '@growth-ui/react/elements/Input';
import React, { useState } from 'react';

const CurrencyInputExampleIntegration = () => {
  const [value, setValue] = useState(0);
  const [error, setError] = useState('');

  const handleChange = (newValue) => {
    if (newValue >= 100) {
      setError('Too much!');
    } else {
      setError('');
    }

    setValue(newValue);
  };

  return (
    <CurrencyInput
      fixed={2}
      value={value}
      onChange={handleChange}
      renderInput={(params) => (
        <Input
          {...params}
          adornment="$"
          label="Amount"
          error={!!error}
          feedback={error}
        />
      )}
    />
  );
};

export default CurrencyInputExampleIntegration;

